var e3_base_url = 'http://quanganhdo.com/e3/';

var emoSelector = '<select id="emoSelector">';
jQuery.each(packs, function(i, n) {
	emoSelector += '<option value="pack' + i + '">' + eval(packs[i] + '_name') + '</option>';
});
emoSelector += '</select>';

var methodSelector = '<select id="methodSelector"><option value="0">BBcode</option><option value="1">HTML</option><option value="2">URL</option></select>';

// Preparation
jQuery.noConflict();
jQuery('<link rel="stylesheet" href="' + e3_base_url + 'e3.css?mc=' + new Date().getTime() + '" />').appendTo('head');

// Main
var e3_target = null;
var current_pack = jQuery.cookie('e3_current_pack') == null ? 0 : jQuery.cookie('e3_current_pack');
var current_method = jQuery.cookie('e3_current_method') == null ? 0 : jQuery.cookie('e3_current_method');

jQuery('img.e3').remove();
jQuery('textarea:visible').each(function(i, n) {
	var current = this;
	var pos = jQuery(this).offset();
	var top = pos.top + jQuery(this).outerHeight();
	var left = pos.left + jQuery(this).outerWidth() - 33;
	jQuery('<img class="e3" id="e3_' + i+ '" src="' + e3_base_url + 'e3.png" />').appendTo('body');
	jQuery('#e3_' + i).css({
		position: 'absolute',
		top: top,
		left: left,
		cursor: 'pointer',
		border: 'solid 1px',
		'z-index': '999'
	}).click(function() {
		e3_target = current;
		showPack(packs[current_pack]);
	});
});

// Extra
function showPack(name) {
	var bu = eval(name + '_base_url');
	var emo = jQuery.map(eval(name + '_files'), function(i) {
		return '<img class="' + name + '" src="' + bu + i + '" />';
	});
	jQuery.prompt('<div align="center" class="text">Pack: ' + emoSelector + ' Insert Method: ' + methodSelector + '</div><div class="e3">' + emo.join('') + '</div><div align="center" class="text">Bookmarklet coded by <a href="http://quanganhdo.com" target="_blank">QAD</a> &bull; Emoticons collected by <a href="http://emo.buzzbanbe.net/default.aspx" target="_blank">Buzz Ban Be Team</a></div>', {
		buttons: {
			Cancel : true
		}
	});
	jQuery('img.' + name).each(function(i, n) {
		jQuery(this).click(function() {
			insertEMO(toCode(jQuery(this)[0].src), jQuery(e3_target)[0]);
			jQuery('#jqibuttonCancel').click();
		});
	});
	jQuery('#emoSelector option').each(function(i, n) {
		jQuery(this).click(function() {
			jQuery('#jqibuttonCancel').click();
			current_pack = i;
			jQuery.cookie('e3_current_pack', current_pack, {expires: 365});
			showPack(packs[i]);
		});
	});
	jQuery('#methodSelector option').each(function(i, n) {
		jQuery(this).click(function() {
			current_method = jQuery(this).val();
			jQuery.cookie('e3_current_method', current_method, {expires: 365});
		});
	});
	jQuery('#emoSelector option')[current_pack].selected = 'selected';
	jQuery('#methodSelector option')[current_method].selected = 'selected';
}

function toCode(src) {
	if (current_method == 0) { // bbcode
		return '[IMG]' + src + '[/IMG]';
	} else if (current_method == 1) { // html
		return '<img src="' + src + '" />';
	} else if (current_method == 2) { // url
		return src;
	}
}

function insertEMO(emo, target) {
	if (document.selection) {
		target.focus();
		sel = document.selection.createRange();
		sel.text = emo;
		target.focus();
	} else if (target.selectionStart || target.selectionStart == '0') {
		var startPos = target.selectionStart;
		var endPos = target.selectionEnd;
		var cursorPos = endPos;
		target.value = target.value.substring(0, startPos)
					  + emo
					  + target.value.substring(endPos, target.value.length);
		cursorPos += emo.length;
		target.focus();
		target.selectionStart = cursorPos;
		target.selectionEnd = cursorPos;
	} else {
		target.value += emo;
		target.focus();
	}
}